import styled from "styled-components"

export const Container = styled.div`
  position: absolute;
  padding-top: 150px;
  box-sizing: border-box;
  //left: 480px;
  top: 0;
  height: 100%;
  width: 100%;
  min-width: 1800px;
  //width: 1600px;
  background-color: #f5f5f5;
  z-index: 90;
`

export const Content = styled.div`
  background-color: #bfc;
  position: absolute;
  width: 800px;
  height: 420px;
  top: 200px;
  left: 0;
  right: 0;
  margin: 0 auto;

`
